    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
        try{
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/lms","root","");
            Statement st=con.createStatement();
            ResultSet rs = st.executeQuery("select issue.StudentID,student_details.studentName,issue.bookID,book.name1,issue.issuedate, issue.duedate from student_details inner join book inner join issue where book.bookID=issue.bookID and student_details.StudentID=issue.StudentID and issue.returnbook='NO'");
            jTable1.setModel(DbUtils.resultSetToTableModel(rs));
            ResultSet rs1 = st.executeQuery("select issue.StudentID,student_details.studentName,issue.bookID,book.name1,issue.issuedate, issue.duedate from student_details inner join book inner join issue where book.bookID=issue.bookID and student_details.StudentID=issue.StudentID and issue.returnbook='YES'");
            jTable2.setModel(DbUtils.resultSetToTableModel(rs1));
            
        }
        catch(Exception e){
        e.printStackTrace();
        JOptionPane.showMessageDialog(null,"Connection Error");
        
        }
    }//GEN-LAST:event_formComponentShown

